import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, Redirect } from 'react-router-dom';
import ProductList from './components/ProductList';
import AddProduct from './components/AddProduct';
import Cart from './components/Cart';
import PurchaseSuccess from './components/PurchaseSuccess';
import Login from './components/Login';
import Register from './components/Register';
import './App.css';

function App() {
    const [cart, setCart] = useState([]);
    const [user, setUser] = useState(null);

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('user');
        setUser(null);
        setCart([]);
    };

    return (
        <Router>
            <div className="App">
                <nav>
                    <ul>
                        <li><Link to="/">Products</Link></li>
                        {user ? (
                            <>
                                <li><Link to="/add-product">Add Product</Link></li>
                                <li><Link to="/cart">Cart ({cart.length})</Link></li>
                                <li><span className="user-name">Welcome, {user.username}</span></li>
                                <li><button onClick={handleLogout}>Logout</button></li>
                            </>
                        ) : (
                            <>
                                <li><Link to="/login">Login</Link></li>
                                <li><Link to="/register">Register</Link></li>
                            </>
                        )}
                    </ul>
                </nav>
                
                <Switch>
                    <Route exact path="/" render={(props) => <ProductList {...props} cart={cart} setCart={setCart} />} />
                    <Route path="/login" render={(props) => <Login {...props} setUser={setUser} />} />
                    <Route path="/register" component={Register} />
                    <Route path="/add-product">
                        {user ? <AddProduct /> : <Redirect to="/login" />}
                    </Route>
                    <Route path="/cart">
                        {user ? <Cart cart={cart} setCart={setCart} /> : <Redirect to="/login" />}
                    </Route>
                    <Route path="/purchase-success" component={PurchaseSuccess} />
                </Switch>
            </div>
        </Router>
    );
}

export default App;
